import { PostHog } from 'posthog-node';
import { TelemetryEvents } from './events.js';

/**
 * Telmetry for the CLI.
 *
 * Auto injects the git origin url.
 */
export declare const telemetry: Omit<PostHog, "capture"> & {
    capture: typeof capture;
};
/**
 * Wrapper to auto inject the git origin url and user id.
 */
declare function capture(args: CaptureEventArguments, projectId?: string): void;
/**
 * Typesafe wrapper around the `telemetryNode.capture` method.
 *
 * Exists to avoid typos/always set the correct event name and properties.
 */
type CaptureEventArguments = Omit<Parameters<PostHog["capture"]>[0], "distinctId" | "groups"> & {
    event: TelemetryEvents;
};
export {};
