export class ALSContext {
    ctx;
    constructor(ALS) {
        this.ctx = new ALS();
    }
    get() {
        return this.ctx.getStore();
    }
    async callAsync(val, cb) {
        return await this.ctx.run(val, cb);
    }
}
export class GlobalContext {
    value = undefined;
    get() {
        return this.value;
    }
    async callAsync(val, cb) {
        this.value = val;
        return await cb();
    }
}
