export function getElementsFromAst(ast, elementName) {
    const links = [];
    function walk(templateNode) {
        if (templateNode.type === "Element" &&
            "name" in templateNode &&
            templateNode.name === elementName) {
            links.push(templateNode);
        }
        for (const child of "children" in templateNode ? templateNode.children : []) {
            walk(child);
        }
        // @ts-ignore
        if (templateNode.else)
            walk(templateNode.else);
        // @ts-ignore
        if (templateNode.then)
            walk(templateNode.then);
        // @ts-ignore
        if (templateNode.catch)
            walk(templateNode.catch);
    }
    walk(ast.html);
    return links;
}
export function hasSpreadAttribute(element) {
    return element.attributes.some((attribute) => attribute.type === "Spread");
}
export function getAttributeByName(element, name) {
    return element.attributes.find((attribute) => attribute.type === "Attribute" && attribute.name === name);
}
