import { statusList as lixStatusList } from "./status-list.js";
export async function status(ctx, state, filepath) {
    if (typeof filepath !== "string") {
        throw new Error("parameter must be a string");
    }
    const statusList = await lixStatusList(ctx, state, {
        filepaths: [filepath],
    });
    const maybeStatusEntry = statusList[0] || [filepath, "unknown"];
    return maybeStatusEntry?.[1];
}
